choose.when:
AND (
a.title LIKE CONCAT('%', #{text}, '%')  OR a.content LIKE CONCAT('%', #{text}, '%')
)

mapper.insert:
INSERT INTO tb_board (
   title
  ,content
  ,thumb_id
  ,passwd
  ,reg_id
  ,reg_ip
) VALUES (
   #{title}
  ,#{content}
  ,#{thumbId}
  ,SHA2(#{passwd}, 256)
  ,(CASE WHEN #{regId} = '' THEN NULL ELSE #{regId} END)
  ,#{regIp}
)

mapper.update:
UPDATE tb_board
SET
     title = #{title}
    ,content = #{content}
    ,thumb_id = #{thumbId}
    ,upd_dt = current_timestamp()
WHERE 1=1
AND brd_no = #{brdNo}

mapper.delete:
DELETE FROM tb_board_category
WHERE brd_no = #{brdNo}

    AND cat_no NOT IN

        #{catNo}
